    val authorizationResource = mock[AuthorizationResource]
    val authorizationResponse = mock[AuthorizationResponse]
    val tokenStr =
      "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJudHRFLUFaRGxhTThYdWt6QlhXWnJKYkFPNDRwM1pSSUFWWDVWQXpOQlMwIn0..IOtHNp4LRqEz8AFkH5g1EQAYNN1kbex35wo9JIEcSTQe-NKKNFdr_yr1LlarIbatCEUrSXi9vV3RmX0CY2F2bkIbUhzYJKubI0tw5Ym3xcc_LSEQFNJu00_H-kQ3AVrgg__mfuHlmF5vTAdu_eaSoLnuirPNre_LwZPqBDcNVaYQt1tXXt2E4lD8Thbdgl5c0HcdaN7XzJGuPHkE3GxhKVqrlvsRoTvVaMLmyW-qIAQm8Nuu_GfUbZpxUhITtyP5b_tWMCzRfiXknGIWHjVwfeASN8_geXZDH8S8UEa3Qmv13ByPM8mdnZrzNYNRQlnIScFYCCRbIsP3eyEIhZBp3g"

    val rptStr =
      "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJudHRFLUFaRGxhTThYdWt6QlhXWnJKYkFPNDRwM1pSSUFWWDVWQXpOQlMwIn0..hJHMycSHTGNoSREX3yhw0akxM59j4yCUN_gLfdkwWyvvpWoPoSL4zrkKk7xwO6C9NeyfjZKYTlBvVG7hV-XQnQhT9Oq7zTQrniv0vDSkna8ldkevgNnVFIJ-dMEL0VfFd0PrPSzHRWJHR_3yOE-nZ3UvJde2m0E-OpQAx_qjHZN66A6O2KkURP2hB4lg_N0AyizjVpLwAGDKksBGsn0SPeohFE8r6R7xbcu8__4OJTA_WKHppJeO4WtY0f9zGyZXi6wtNO0lHCerfLtBNJJFdrVkIpRGjNgsQHBe_gJJ0Cm-wxf_8A6CFP7qqKIpVlVEClScBJHLIWq-mieQxbT7hw"

    val expectedPRT = AccessToken(
      value = rptStr,
      sub = Option("24c0ae78-d2d3-4955-b71f-f6e6cf7dea78"),
      iat = Option(1543474907),
      exp = Option(1543475507),
      iss = Option("http://localhost:8080/auth/realms/example"),
      aud = Audience(List("example-app", "example-server")),
      jti = Option("f493fc38-735a-4c3d-a2e7-5928e8a8e0c1"),
      given_name = Option("test-user"),
      family_name = None,
      name = Option("test-user"),
      preferred_username = Option("test-user"),
      email = None,
      scope = Option("profile email"),
      realm_access = Access(Set("offline_access", "uma_authorization", "example-admin-role")),
      resource_access = Map(
        "account"        -> Access(Set("manage-account", "manage-account-links", "view-profile")),
        "example-server" -> Access(Set("person-role"))
      ),
      authorization = Authorization(
        Set(
          Permission("f1d4514d-4bfb-48e2-9448-f92a94ffca4d", "person", Set("delete")),
          Permission("e70cc08b-5ead-4228-afb3-a75503c1bf37", "Default Resource", Set.empty)
        )
      )
    )
    val rpt = RPT(authzClient)

    when(authzClient.authorization(tokenStr)).thenReturn(authorizationResource)
    when(authorizationResource.authorize()).thenReturn(authorizationResponse)
    when(authorizationResponse.getToken).thenReturn(rptStr)

    rpt.create(tokenStr).futureValue shouldEqual Right(expectedPRT)
  }

  test("should fail for creating accessToken") {
    val authzClient           = mock[AuthzClient]
    val authorizationResource = mock[AuthorizationResource]
    val token                 = "asd.adsasd.asd.qwe"
    val rpt                   = RPT(authzClient)

    when(authzClient.authorization(token)).thenReturn(authorizationResource)
    when(authorizationResource.authorize())
      .thenThrow(new AuthorizationDeniedException("token is invalid", new RuntimeException))

    a[AuthorizationDeniedException] shouldBe thrownBy(Await.result(rpt.create(token), 5.seconds))
  }
}
